```objc
+ (CGContextRef) newBitmapRGBA8ContextFromImage:(CGImageRef) image {
	CGContextRef context = NULL;
	CGColorSpaceRef colorSpace;
	uint32_t *bitmapData;

	size_t bitsPerPixel = 32; // 4(RGBA)1byte432bit
	size_t bitsPerComponent = 8; //bit
	size_t bytesPerPixel = bitsPerPixel / bitsPerComponent; //byte

	size_t width = CGImageGetWidth(image); 
	size_t height = CGImageGetHeight(image);

	size_t bytesPerRow = width * bytesPerPixel; //
	size_t bufferLength = bytesPerRow * height; //buffersize

	colorSpace = CGColorSpaceCreateDeviceRGB(); //RGB

	if(!colorSpace) {
		NSLog(@"Error allocating color space RGB\n");
		return NULL;
	}

	// 
	bitmapData = (uint32_t *)malloc(bufferLength);

	if(!bitmapData) {
		NSLog(@"Error allocating memory for bitmap\n");
		CGColorSpaceRelease(colorSpace);
		return NULL;
	}

	// bitmap context
	context = CGBitmapContextCreate(bitmapData, 
									width, 
									height, 
									bitsPerComponent, 
									bytesPerRow, 
									colorSpace, 
                                    kCGImageAlphaPremultipliedLast);	// RGBA

	if(!context) {
        free(bitmapData);
        NSLog(@"Bitmap context not created");
    }

  CGColorSpaceRelease(colorSpace);
	return context;	
}
```

